import getConfig from 'next/config';
import Airtable from 'airtable';
import { verify } from 'jsonwebtoken';

const { serverRuntimeConfig } = getConfig();

export default async (req, res) => {
  const { base, table, initialState } = verify(req.body.jwt, serverRuntimeConfig.appSecret);
  const at = new Airtable({ apiKey: serverRuntimeConfig.airtableKey }).base(base)(table);
  const result = await (await at.select({
    maxRecords: 1,
    filterByFormula: `{Status} = '${initialState}'`,
    sort: [{field: "Cognito ID", direction: "asc"}],
    fields: [
      "Track",
      "Timezone",
      "Prior Experience",
      "Past Project",
      "Anything Else",
      "LinkedIn",
      "Resume",
      "Student Type",
      "School",
      "Underrepresented",
      "Cognito ID",
    ]
  })).firstPage();
  res.send((result[0] || {fields:{}}).fields);
}
